import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { AircraftsService } from 'src/app/services/aircrafts/aircrafts.service';

@Component({
  selector: 'app-add',
  templateUrl: './add.component.html',
  styleUrls: ['./add.component.css']
})
export class AddComponent implements OnInit {
  aircraftForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private aircraftService: AircraftsService) {
    this.aircraftForm = this.formBuilder.group({
      modelName: [''],
      serialNumber: [''],
      registrationNumber: [''],
      registrationDate: [''],
      registrationStatus: [''],
    });

  }

  ngOnInit(): void {

  }

  formatDate(dateToFormat: any) {
    return new Date(dateToFormat).toISOString();
  }

  async submitForm() {
    this.aircraftService.add(this.aircraftForm.value);
  }

}
